import {
    GET_PET,
    GET_PETS,
    PET_ERROR,
    CLEAR_PET,
    DELETE_PET,
    PET_PHOTOS
     } from "../actions/consts"

const initialState ={
    pet: null,
    pets: [],
    photos:[],
    loading: true,
    error: {}
}



export default function(state = initialState, action){
    const {type, data} = action


    switch(type){
        case GET_PET:
            return{
                ...state,
                pet: data,
                loading: false 
            }
        case GET_PETS:
            return{
                ...state,
                pets: data,
                loading: false 
            }
            case PET_PHOTOS:
                return{
                    ...state,
                    photos: data
                }
        case PET_ERROR:
            return {
                ...state,
                pets:null,
                photos: null,
                error: data,
                loading: false
            }
        case CLEAR_PET:
            return {
                ...state,
                profile: null,
                loading: false
            }
