import React, { Fragment, useEffect, useState } from 'react'
import PropTypes from 'prop-types'
import Dropzone from '../../../drag&drop/Dropezone'
import {connect} from 'react-redux'
import { withRouter, Link} from 'react-router-dom'
import {getUserProfile, createPictureProfile} from '../../../../actions/profileActs'
import axios from 'axios'




const SetUpPicture = ({
    profile: {profile, loading},
    createPictureProfile,
     history}) => {

    const [image, setImage] = useState('')
    const [imageName, setImageName] = useState('choose file')
    const [uploadedImage, setUploadedImage] = useState({})
  

    const onDrop = e =>{
        const file = e[0]
        setImage(e[0])
        setImageName(e[0].name)
        getBase64(file).then(base64 => {
            localStorage["fileBase64"] = base64;
            console.log("file stored",base64);
          });
      };
        //save mage localy?
      
    
      const getBase64 = (file) => {
        return new Promise((resolve,reject) => {
           const reader = new FileReader();
           reader.onload = () => resolve(reader.result);
           reader.onerror = error => reject(error);
           reader.readAsDataURL(file);
           console.log(file+'file')
        })}

    const sentData = async e =>{
        e.preventDefault()
        createPictureProfile
        //go to profileActs thant fubction that sent req with image than convert to base 64 and sve in mongoose DB 
        const formData = new FormData()
        formData.append('image', image)
        try {
            const res = await axios.post('http://localhost:5000/api/profile/profilePicture', formData, {
                headers: {
                    'Content-Type': 'multipart/from-data'
                  }
            }) 
            
              const {imageSaved} =res.data
            setUploadedImage({imageSaved})
            console.log(imageSaved)
      } catch(err){
          if(err.response.status === 500){
              console.log('server probl1m')
          }
      }
    }

    return (
        <Fragment>
        <form className="form" onSubmit= {e => sentData(e)}>
         <div className="form-group">
        <h3>Set up a profile picture!</h3><br/>
        <h1 className="text-center">Drag and Drop Example</h1>
        <img className='round-img'  /> 
        <Dropzone onDrop={(e) => onDrop(e)} accept={"image/*"} multiple={false}/>
        </div>
        <label htmlFor="custmFile">{imageName}</label>
     {uploadedImage ? <div mt-5>
	    <div className="box-2">
		<div className="result"></div>
    	</div>
	    <div className="box-2 img-result hide">
		    <img className="cropped" src="" alt=""/>
	    </div>
    	<div className="box"/>
		<div className="options hide"/>
        </div>: null}
        <input type="submit" className="btn btn-primary my-1"  value='Submit'/>
        <button className="btn btn-light my-1"> <Link to='/profiles/editProfile'>Go Back</Link></button>
      </form>
    </Fragment>
    )
     }

const mapStateToProps = state => ({
    profile: state.profile
})

SetUpPicture.propTypes = {
    createPictureProfile: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    getUserProfile: PropTypes.func.isRequired,

}

export default connect(
    mapStateToProps,
    {createPictureProfile, getUserProfile}) (withRouter(SetUpPicture))
