import axios from 'axios'
import {setAlert} from './alertAct'
import{
    GET_PET,
    GET_PETS,
    PET_ERROR,
    PET_PHOTOS
  } from './consts'


//create or updite pet profile
export const createPet = (petFormData, history, edit = false) => async dispatch => {
    try {
        const config = {
            headers: {
              'Content-Type': 'application/json'
            }
          }

        const res = await axios.post('http://localhost:5000/api/pets', petFormData, config)

         dispatch({
            type: GET_PET,
            data: res.data
        })

        dispatch(setAlert(edit ? 'Pet Profile Updated' : 'Pet Profile Created', 'success'))

        history.push('/dashboard')
        
    } catch (err) {
        const errors = err.response.data.errors;
  
        if (errors) {
          errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PET_ERROR,
            data: {
                msg: err.response.statusText,
                status: err.response.status
            }
        })
    }
}


//get user pet profile
export const getPetById = petId => async dispatch => {
  try{
    const res = await axios.get(`http://localhost:5000/api/pets/${petId}`)
  
      dispatch({
          type: GET_PET,
          data: res.data
      })
  } catch (err){
      dispatch({
          type: PET_ERROR,
          data: {
              msg: err,
              status: err
      }
      })
  }}


//add photos to pet
export const addPetImages = (image, id)  => async dispatch =>{

