import axios from 'axios'
import { setAlert} from './alertAct'
import {
    GET_POSTS,
    GET_POST,
    POST_ERROR
} from './consts'


//get all the posts
export const getPosts = () => async dispatch =>{
    try {
        const res = await axios.get('/api/posts')

        dispatch({
            type: GET_POSTS,
            data: res.data
        })
    } catch (err) {
        dispatch({
            type: POST_ERROR,
            data: { 
                msg: err,
                status: err
            }
        })
    }
}

// add like
export const addLike = postId => async dispatch =>{
    try {
        const res = await axios.get('/api/posts/like/${postI')

        dispatch({
            type: GET_POSTS,
            data: res.data
        })
    } catch (err) {
        dispatch({
            type: POST_ERROR,
            data: { 
                msg: err,
                status: err
            }
        })
    }
}
