import React, { Fragment, useCallback, useEffect, useState } from 'react'
import PropTypes from 'prop-types'
import Dropzone from '../../../drag&drop/Dropezone'
import {connect} from 'react-redux'
import { withRouter, Link} from 'react-router-dom'
import {getUserProfile, createPictureProfile} from '../../../../actions/profileActs'
import Cropper from 'react-easy-crop'


const SetUpPicture = ({
    profile: {profile, loading},
    createPictureProfile,
     history}) => {

    const [image, setImage] = useState('')
    const [crop, setCrop] = useState({ x: 0, y: 0 })
    const [zoom, setZoom] = useState(1)
    const [rotation, setRotation] = use
    const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
      console.log(croppedArea, croppedAreaPixels, zoom)
    }, [])

    const getBase64 = (image) => {
        return new Promise((resolve,reject) => {
           const reader = new FileReader();
           reader.onload = () => resolve(reader.result);
           reader.onerror = error => reject(error);
           reader.readAsDataURL(image);
        })}


    const onDrop = e =>{
        setImage(e[0])
      }
        //save mage localy?
      
    

    const sentData = async e =>{
        e.preventDefault()
        getBase64(image).then(convertedImage => {
            createPictureProfile(convertedImage, history)
            
        })
        //that sent req with image than convert to base 64 and sve in mongoose DB 
    }

    return (
        <Fragment>
        <form className="form" onSubmit= {e => sentData(e)}>
         <div className="form-group">
        <h1 className="text-center">Set up a profile picture</h1>
        <img className='round-img'  /> 
        <Dropzone onDrop={(e) => onDrop(e)} accept={"image/*"} multiple={false}/>
        </div>
     {image ? <div>
      <div className="crop-container">
        <Cropper
          image="https://img.huffingtonpost.com/asset/5ab4d4ac2000007d06eb2c56.jpeg?cache=sih0jwle4e&ops=1910_1000"
          crop={crop}
          zoom={zoom}
          aspect={4 / 3}
          onCropChange={setCrop}
          onCropComplete={onCropComplete}
          onZoomChange={setZoom}
        />
        </div>
        <div className="controls">
        <input 
            type="range" 
            min={1} 
            max={3}
            value={zoom}
            step={0.1}
            onChange={(e) => setZoom(e.target.value)}
        />
        <input 
            type="range" 
            min={0} 
            max={360}
            value={rotation}
            step={1}
            onChange={(e) => setRotation(e.target.value)}
        />
      </div>
      </div>: null}
        <input type="submit" className="btn btn-primary my-1"  value='Submit'/>
        <button className="btn btn-light my-1"> <Link to='/profiles/editProfile'>Go Back</Link></button>
      </form>
    </Fragment>
    )
     }

const mapStateToProps = state => ({
    profile: state.profile
})

SetUpPicture.propTypes = {
    createPictureProfile: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    getUserProfile: PropTypes.func.isRequired,

}

export default connect(
    mapStateToProps,
    {createPictureProfile, getUserProfile}) (withRouter(SetUpPicture))
