import React, {useEffect} from 'react'
import PropTypes from 'prop-types'
import Grid from '@material-ui/core/Grid';
import {connect} from 'react-redux'
import {addPetImages} from '../../actions/petActs'

const PetImage = ( {addPetImages,
    image:{
            src,
        },
        petId
    }) => {
        useEffect(() => {
                addPetImages({src, petId})
            }, [ petId])
        
    return (
        <Grid item xs={12} sm={6} lg={4}>
            <img  src={src}/>
            <textarea placeholder="Tell As About The Moment You Took The Photo " 
          onChange={(e) => addInfo(e, image.id)}></textarea> 
        </Grid>
    )
}

const mapStateToProps = state =>({
    petId: state.pets.pet._id
})

PetImage.propTypes = {
    src: PropTypes.string.isRequired,
    petId: PropTypes.string.isRequired,
    addPetImages: PropTypes.func.isRequired
}


export default connect(mapStateToProps,
    {addPetImages})(PetImage)
