import {
    GET_PET,
    GET_PETS,
    PET_ERROR,
    CLEAR_PET
     } from "../actions/consts"

const initialState ={
    pet: null,
    pets: null,
    error: {}
}



export default function(state = initialState, action){
    const {type, data} = action


    switch(type){
        case GET_PET:
            return{
                ...state,
                pet: data,
                owner: data.user,
                loading: false 
            }
        case GET_PETS:
            return{
                ...state,
                pets: data,
                loading: false 
            }
        case PET_ERROR:
            return {
                ...state,
                error: data,
                loading: false
            }
        case CLEAR_PET:
            return {
                ...state,
                profile: null,
                loading: false
            }
        default:
            return state
    }
}
