import React, { Fragment, useCallback, useState } from 'react'
import PropTypes from 'prop-types'
import Dropzone from '../../../drag&drop/Dropezone'
import {connect} from 'react-redux'
import { withRouter, Link} from 'react-router-dom'
import {getUserProfile, createPictureProfile} from '../../../../actions/profileActs'
import Cropper from 'react-easy-crop'
import getCroppedImg from '../../../../cropImage'


const SetUpPicture = ({
    createPictureProfile,
     history}) => {
    const [dropStyle, setDropStyle] = useState('bigDropzone')
    const [image, setImage] = useState('')
    const [crop, setCrop] = useState({ x: 0, y: 0 })
    const [zoom, setZoom] = useState(1)
    const [rotation, setRotation] = useState(0)
    const [croppedAreaPixels, setCroppedAreaPixels] = useState(null)
    const onCropComplete = useCallback((croppedArea, croppedAreaPixels) => {
        setCroppedAreaPixels(croppedAreaPixels)
      }, [])

    const getBase64 = (image) => {
        return new Promise((resolve,reject) => {
           const reader = new FileReader();
           reader.onload = () => resolve(reader.result);
           reader.onerror = error => reject(error);
           reader.readAsDataURL(image)
        })}


    const onDrop =async e =>{
           setImage(await getBase64(e[0]))
           setCrop({ x: 0, y: 0})
           setZoom(1)
           setRotation(0)  
           setDropStyle
      }
      
    

    const sentData = async (e) => {
        e.preventDefault()
        const croppedImage = await getCroppedImg(
                image,
                croppedAreaPixels,
                rotation
        )
        createPictureProfile(croppedImage , history)
        }
                

    return (
        <Fragment>
        <form className="form" onSubmit= {e => sentData(e)}>
         <div className="form-group">
        <h1 className="text-center">Set up a profile picture</h1>
        <Dropzone onDrop={(e) => onDrop(e)} accept={dropStyle} multiple={false}
        />
        </div>
     {image ? <div>
      <div className="crop-container">
        <Cropper
          image={image}
          crop={crop}
          zoom={zoom}
          rotation = {rotation}
          aspect={1}
          cropShape="round"
          showGrid={false}
          restrictPosition={false}
          onCropChange={setCrop}
          onZoomChange={setZoom}
          onCropComplete={onCropComplete}
        />
        </div>
        <div className="controls">
        <input 
            type="range" 
            min={1} 
            max={3}
            value={zoom}
            step={0.1}
            onChange={(e) => setZoom(e.target.value)}
        />
        <input 
            type="range" 
            min={0} 
            max={360}
            value={rotation}
            step={1}
            onChange={(e) => setRotation(e.target.value)}
        />
      </div>
      </div>: null}
        <input type="submit" className="btn btn-primary my-1"  value='Submit'/>
        <button className="btn btn-light my-1"> <Link to='/profiles/editProfile'>Go Back</Link></button>
      </form>
    </Fragment>
    )
     }

const mapStateToProps = state => ({
    profile: state.profile
})

SetUpPicture.propTypes = {
    createPictureProfile: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    getUserProfile: PropTypes.func.isRequired,

}

export default connect(
    mapStateToProps,
    {createPictureProfile, getUserProfile}) (withRouter(SetUpPicture))
