import React, {useEffect, Fragment} from 'react'
import {Link} from 'react-router-dom'
import PropTypes from 'prop-types'
import {connect} from 'react-redux'
import {getUserProfile} from '../../actions/profileActs'
import spinner from '../../img/spinner.gif'
import DashLinks from './DashLinks'

const Dashboard = ({
    getUserProfile,
    auth: {user},
    profile:{profile, loading}}) => {
    useEffect(() => {
        getUserProfile()
      }, [])

    return (
        loading && profile === null ? 
        <img src={spinner} alt='Loading...'
        /> : <Fragment>
                <h1 className='large text-primary'>Home Page</h1>
                <p className='lead'> 
                    <i className='fas fa-user'>Welcome {user && user.name}</i>
                </p>
                
                {profile !== null ? (
                <Fragment>
                    <DashLinks/>
                </Fragment>
                ) :(
                <Fragment>
                    <p>You don't have a profile,</p>
                    <p>for setting one, please click on "Create Profile"</p><br/>
                    <Link to='/profiles/new' className='btn btn-primary my-1'>
                        Create Profile
                    </Link>
                </Fragment>
                )}
             </Fragment>
    )
}

const mapStateToProps = state =>({
    auth: state.auth,
    profile: state.profile
})

Dashboard.propTypes = {
    getUserProfile: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    profile: PropTypes.object.isRequired
}

export default connect(
    mapStateToProps,
    { getUserProfile})(Dashboard)
