import React, { useEffect, useState } from 'react'
import PropTypes from 'prop-types'
import Dropzone from '../../../drag&drop/Dropezone'
import {connect} from 'react-redux'
import { withRouter, Link} from 'react-router-dom'
import {getUserProfile, createPictureProfile} from '../../../../actions/profileActs'




const SetUpPicture = ({
    profile: {profile, loading},
    createPictureProfile,
     history}) => {

    const [formData, setFormData] = useState({

    profileImage: ''
    })


    useEffect(() => {
        getUserProfile()
        if(profile.profileImage){
            console.log("profileImage: "+ profile.profileImage)
            setFormData({profileImage: profile.profileImage})
        }
    },[loading])
    const  { 
        profileImage
  } = formData

    const onDrop = e =>{
        console.log(e[0] + 'p')
        setFormData({profileImage: e[0]})
      }
    
    const sentData = e =>{
        e.preventDefault()
        createPictureProfile(formData, history)
      }


    return (
        <form className="form" onSubmit= {e => sentData(e)}>
         <div className="form-group">
        <h3>Set up a profile picture!</h3><br/>
        <h1 className="text-center">Drag and Drop Example</h1>
        <img className='round-img'  src={profileImage}/> 
        <Dropzone onDrop={(e) => onDrop(e)} accept={"image/*"} multiple={false}/>
        </div>
        <input type="submit" className="btn btn-primary my-1"  value='Submit'/>
        <button className="btn btn-light my-1"> <Link to='/profiles/editProfile'>Go Back</Link></button>
      </form>
    )
}

const mapStateToProps = state => ({
    profileImage: state.profile.profileImage
  })

SetUpPicture.propTypes = {
    createPictureProfile: PropTypes.func.isRequired,
    profileImage: PropTypes.string.isRequired,
    getUserProfile: PropTypes.func.isRequired,

}

export default connect(
    mapStateToProps,
    {createPictureProfile, getUserProfile}) (withRouter(SetUpPicture))
