import React from 'react'
import PropTypes from 'prop-types'
import Dropzone from '../../../drag&drop/Dropezone'
import {connect} from 'react-redux'
import { withRouter} from 'react-router-dom'





function SetUpPicture(createPictureProfile, history) {
    return (
         <div className="form-group">
        <h3>Set up a profile picture!</h3><br/>
        <h1 className="text-center">Drag and Drop Example</h1>
        <img className='round-img'  src={profileImage}/> 
        <Dropzone onDrop={(e) => onDrop(e)} accept={"image/*"} multiple={false}/>
        </div>
    )
}

SetUpPicture.propTypes = {
    createPictureProfile: PropTypes.func.isRequired,

}

export default connect(null,
    {createPictureProfile}) (withRouter(SetUpPicture))
