import {
     GET_PROFILE,
      PROFILE_ERROR,
       CLEAR_PROFILE,
       PROFILE_PICTURE_ERROR,
       GET_PROFILE_PICTURE
     } from "../actions/consts"

const initialState ={
    profile: null,
    profiles: [],
    profileImage,
    loading: true,
    error: {}
}



export default function(state = initialState, action){
    const {type, data} = action


    switch(type){
        case GET_PROFILE:
            return{
                ...state,
                profile: data,
                loading: false 
            }
        case PROFILE_PICTURE_ERROR:
        case PROFILE_ERROR:
            return {
                ...state,
                error: data,
                loading: false
            }
        case CLEAR_PROFILE:
            return {
                ...state,
                profile: null,
                loading: false
            }
        case GET_PROFILE_PICTURE:
                return{
                    ...state,
                    profileImage: data,
                    loading: false 
                }
        default:
            return state
    }
}
