import{
    GET_POSTS,
    POST_ERROR,
    GET_POST,
    ADD_POST,
    DELETE_POST,
    UPDATE_LIKES,
    ADD_COMMENT,
    REMOVE_COMMENT
} from '../actions/consts'

const inItialState ={
    posts: [],
    post: null,
    loading: true,
    error: {}
}

export default function(state = inItialState, action) {
    const {type, data} = action

    switch(type) {
        case GET_POSTS:
            return{
            ...state,
            posts: data,
            loading: false
            }
        case GET_POST:
            return{
                ...state,
                post: data,
                loading:false
            }
        case ADD_POST:
            return{
                ...state,
                posts: [data, ...state.posts],
                loading: false
            }
        case DELETE_POST:
            return {
                ...state,
                posts: state.posts.filter(post => post._id !== data),
                loading: false
            }
        case UPDATE_LIKES:
            return{
                ...state,
                posts: state.posts.map(post => 
                    post._id === data.postId ? 
                    {...post, likes: data.likes} : post),
                loading: false
            }
        case POST_ERROR:
            return{
                ...state,
                error: data,
                loading:false
            }
        case ADD_COMMENT:
            return{
                ...state,
                post: {...state.posts, comments: data},
                loading: false
            }
        case REMOVE_COMMENT:
            return {
                ...state,
                post:{
                    ...state.post,
                     comments: state.post.comm}
            }
        default:
            return state
    }
}
