import { GET_PROFILE, PROFILE_ERROR, CLEAR_PROFILE } from "../actions/consts"

const initialState ={
    profile: null,
    profiles: [],
    loading: true,
    error: {}
}



export default function(state = initialState, action){
    const {type, data} = action


    switch(type){
        case GET_PROFILE:
            return{
                ...state,
                profile: data,
                loading: false 
            }
        case PROFILE_ERROR:
            return {
                ...state,
                error: data,
                loading: false
            }
        default:
            return state
    }
}
