import axios from 'axios'
import {setAlert} from './alertAct'

import{
    GET_PROFILE,
    PROFILE_ERROR,
    GET_PROFILE_PICTURE,
    ERROR_PROFILE_PICTURE
} from './consts'

export const getUserProfile = () => async dispatch => {
try{
  const res = await axios.get('http://localhost:5000/api/profile/me')

    dispatch({
        type: GET_PROFILE,
        data: res.data
    })
} catch (err){
    dispatch({
        type: PROFILE_ERROR,
        data: {
            msg: err.response.statusText,
            status: err.response.status
    }
    })

}
}
//create or updite profile
export const createProfile = (FormData, history, edit = false) => async dispatch => {
    try {
        const config = {
            headers: {
              'Content-Type': 'application/json'
            }
          }

        const res = await axios.post('http://localhost:5000/api/profile', FormData, config)

        dispatch({
            type: GET_PROFILE,
            data: res.data
        })

        dispatch(setAlert(edit ? 'Profile Updated' : 'Profile Created', 'success'))
        if(!edit){
            history.push('/profiles/profilePicture')
        } 

        history.push('/dashboard')
        
    } catch (err) {
        const errors = err.response.data.errors;
  
        if (errors) {
          errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            data: {
                msg: err.response.statusText,
                status: err.response.status
        }
    })
    }
}

export const getUserProfilePicture = () => async dispatch => {
    try{
      const res = await axios.get('http://localhost:5000/api/profile/profilePicture/me')
    
        dispatch({
            type: GET_PROFILE_PICTURE,
            data: res.data
        })
        console.log(res)
    } catch (err){
        dispatch({
            type: PROFILE_PICTURE_ERROR,
            data: {
                msg: err.response.statusText,
                status: err.response.status
        }
        })
    
    }
    }

//create or updite profile picture
export const createPictureProfile = (image, history) => async dispatch => {
    if(!image){
        dispatch(setAlert('You should upload a photo, or click "skip"', 'danger'))
    }
    else{
    try {
        const config = {
            headers: {
              'Content-Type': 'application/json'
            }
          }

        const data = {
            profileImage: image
        }
        const res = await axios.post(
            'http://localhost:5000/api/profile/profilePicture', data, config)


            dispatch({
            type: GET_PROFILE,
            data: res.data
        })

        dispatch(setAlert('Profile Picture is set', 'success'))

        history.push('/dashboard')
  } catch (err) {
        const errors = err.response.data.errors;
  
        if (errors) {
          errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            data: {
                msg: err.response.statusText,
                status: err.response.status
        }
    })
    }
}}
