import axios from 'axios'
import {setAlert} from './alertAct'

import{
    GET_PROFILE,
    PROFILE_ERROR
} from './consts'

export const getUserProfile = () => async dispatch => {
try{
  const res = await axios.get('http://localhost:5000/api/profile/me')

    dispatch({
        type: GET_PROFILE,
        data: res.data
    })
} catch (err){
    dispatch({
        type: PROFILE_ERROR,
        data: {
            msg: err.response.statusText,
            status: err.response.status
    }
    })

}
}
//create or updite profile
export const createProfile = (FormData, history, edit = false) => async dispatch => {
    try {
        const config = {
            headers: {
              'Content-Type': 'application/json'
            }
          }

        const res = await axios.post('http://localhost:5000/api/profile', FormData, config)

        dispatch({
            type: GET_PROFILE,
            data: res.data
        })

        dispatch(setAlert(edit ? 'Profile Updated' : 'Profile Created', 'success'))
        if(!edit){
            history.push('/profiles/profilePicture')
        } 

        history.push('/dashboard')
        
    } catch (err) {
        const errors = err.response.data.errors;
  
        if (errors) {
          errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            data: {
                msg: err.response.statusText,
                status: err.response.status
        }
    })
    }
}

//create or updite profile picture
export const createPictureProfile = (profileImage, history) => async dispatch => {
   
    try {
        const config = {
            headers: {
              'Content-Type': 'application/json'
            }
          }
        const JsonProfileImage = JSON.parse(profileImage)
        console.log(profileImage+'2')
        const res = await axios.post('http://localhost:5000/api/profile/profilePicture', JsonProfileImage, config) 
          console.log(res)
        dispatch({
            type: GET_PROFILE,
            data: res.data
        })

        dispatch(setAlert('Profile Picture is set', 'success'))

        history.push('/dashboard')
  } catch (err) {
        const errors = err.response.data.errors;
  
        if (errors) {
          errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({
            type: PROFILE_ERROR,
            data: {
                msg: err.response.statusText,
                status: err.response.status
        }
    })
    }
}
